import React, { useEffect, useState } from 'react'
import { Link } from 'react-router-dom'
import { useNavigate } from 'react-router-dom'
import axios from 'axios'
import $ from 'jquery'
import HookUserLogin from './HookUserLogin'

const UserLogin = () => {

    const navigate = useNavigate()

    const[userData, setUserData] = useState([])



    // const {data} = HookUserLogin("http://localhost:300/UserDetailsControllerImpl/getUserDetailsByEmailAndPassword/email/"+userEmail+"/password/"+userPassword);
    // const [data, error] = HookUserLogin("http://localhost:300/UserDetailsControllerImpl/getUserDetailsByEmailAndPassword/email/"+userEmail+"/password/"+userPassword)

    function openUserPage(e){
        e.preventDefault()
        //CHECKING THE CREDINTIALS
        var userEmail  = document.getElementById("userEmail").value;
        var userPassword = document.getElementById("userPassword").value;
        const {data} = HookUserLogin("http://localhost:300/UserDetailsControllerImpl/getUserDetailsByEmailAndPassword/email/"+userEmail+"/password/"+userPassword);
        
        // useEffect(() =>{
        //     const[userData, setUserData] = useState([])
        //     axios.get("http://localhost:300/UserDetailsControllerImpl/getUserDetailsByEmailAndPassword/email/"+userEmail+"/password/"+userPassword)
        //     .then(resp => setUserData(resp.data))
        //     .catch(err => console.log(err))
        // }, [])
        // if(!error){
        //     alert(data)
        // }else{
        //     alert("invalid")
        // }
        alert(data)
        
    }
    return (
        <div>
            <div className="border border-danger loginForm ">
                <form className="form" onSubmit={openUserPage}>

                    <div className='' id="errorMessage"></div>

                    <div className='form-group'>
                        <label htmlFor='userEmail'>Email address</label>
                        <input type="email" id="userEmail" className='form-control' placeholder='Enter email' required/>
                    </div>

                    <div className='form-group'>
                        <label htmlFor='userPassword'>Password</label>
                        <input type="password" id="userPassword" className='form-control' placeholder='Password' required/>
                    </div>

                    <div className="rememberMe mb-2">
                        <div className='remember'><input type="checkbox"/>Remember me</div>
                        <Link to="/forgotPassword" className='forgotPassword'>Forgot Password?</Link>
                    </div>
                
                    <button type="submit" className='login mb-2' >Login</button><br/>
                    <div className="dontHaveAccount">Don't hav an account? <Link to="/register">Register</Link></div>
                </form>
            </div>
        </div>
    ) 
}

export default UserLogin
