import React from 'react'
import { Link, useNavigate } from 'react-router-dom'
import $ from 'jquery'

const AdminLogin = () => {

    const navigate=useNavigate()

    function login(e){
        e.preventDefault()
        navigate("/")
    }
    function openAdminPage(e){
        e.preventDefault()

        navigate("/adminPage")
        var profile  = document.createElement("h3")
        var nam=document.getElementById("adminEmail").value

        $("#adminLogin").css("display","none")
        $("#userLogin").css("dusplay")
        // profile.textContent="button"
        // document.getElementById("adminLogin").remove()
        // document.getElementById("userLogin").remove()
        // profile.innerHTML="Welcome"+nam
        // alert(nam)
        // document.body.appendChild(profile)
        var adminLogout = document.createElement("button")
        adminLogout.setAttribute("id", "adminLogout")
        adminLogout.textContent="Logout"
        adminLogout.onclick=function(){
            navigate("/normalPage")
            document.createElement("link")
        }
        document.getElementById("normalPageRight").appendChild(adminLogout)
    }

  return (
    <div>
        <div className="border border-danger loginForm ">
            <form class="form">
                <div className='form-group'>
                    <label htmlFor='adminEmail'>Email address</label>
                    <input type="email" id="adminEmail" className='form-control' placeholder='Enter email' />
                </div>
                <div className='form-group'>
                    <label htmlFor='adminPassword'>Password</label>
                    <input type="password" id="adminPassword" className='form-control' placeholder='Password' />
                </div>
                <div class="rememberMe mb-2">
                    <div className='remember'><input type="checkbox"/>Remember me</div>
                    <Link to="/forgotPassword" className='forgotPassword'>Forgot Password?</Link>
                </div>
                
                <button type="submit" className='login mb-2' onClick={openAdminPage}>Login</button><br/>
                {/* <div className="dontHaveAccount">Don't hav an account? <Link to="/register">Register</Link></div> */}
            </form>
        </div>

        
    </div>
  )
}

export default AdminLogin
