import React from 'react'
import States from 'C:/MY_PROJECT/final/src/rawData/states.json'
import { useState } from 'react'
import { useEffect } from 'react'
import axios from 'axios'
const ModifyNgo = () => {

    var states = States

    const [ngoData, setNgoData] = useState([])
    useEffect(()=>{
        axios.get("http://localhost:400/NgoDetailsControllerImpl/selectByStateId/AP")
        // .then(res => console.log(res.data))
        .then(x => setNgoData(x.data))
    })

    function getStateId(){
        var stateId="";
        var stateName = document.getElementById("searchState").value;
        for(var i=0; i<states.length; i++){
            if(states[i].state_name==stateName){
                stateId=states[i].state_id;
                break;
            }
        }
        return stateId;
    }

    function getNgoData(){
        var stateId = getStateId();
        var url = "http://localhost:400/NgoDetailsControllerImpl/selectByStateId/"+stateId;
        useEffect(() => {
            axios.get(url)
            .then(resp => setNgoData())
        })
    }

    return (
        <div>
            <div className='searchBar'>
                <form>
                    <div className='form-group'>
                        <label id='searchState'>State Name</label>
                        <select id="searchState" onChange={getNgoData}>
                            <option disabled>--Select--</option>
                            {states.map(item => <option>{item.state_name}</option>)}
                        </select>
                    </div>
                    <div className='form-group'>
                        <label id='searchNgo'>Ngo</label>
                        <select id='searchNgo'>
                            <option disabled>--Select</option>
                            {ngoData.map(item => <option>{item.ngoName}</option>)}
                        </select>
                        
                    </div>
                </form>
            </div>
        </div>
    )
}

export default ModifyNgo
